mapper.insert:
insert  IGNORE into tbl_book
    (
        bookname
        ,authors
        ,publisher
        ,publication_year
        ,isbn13
        ,genre
        ,addition_symbol
        ,vol
        ,class_no
    ) 
value 
 

    (
        #{item.bookname}
        ,#{item.authors}
        ,#{item.publisher}
        ,#{item.publication_year}   
        ,#{item.isbn13}
        ,#{item.genre}
        ,#{item.addition_symbol}
        ,#{item.vol}
        ,#{item.class_no}
        
    )

mapper.update:
        update tbl_book set
            bookname = #{bookname}
        where 
            isbn13 = #{isbn13}
